        private void set_initial_form_load()
        {
            try
            {
                m_obj_trans = get_trans_object(m_fg);
                load_data_to_cbo_bo_tinh();
                // load_data_2_grid();
            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
            
        }
        private void load_data_to_cbo_bo_tinh()
        {
            try
            {
                DS_DM_DON_VI v_ds_dm_don_vi = new DS_DM_DON_VI();
                US_DM_DON_VI v_us_dm_don_vi = new US_DM_DON_VI();
                v_us_dm_don_vi.FillDataset(v_ds_dm_don_vi, "WHERE ID_LOAI_DON_VI = 574");
                m_cbo_bo_tinh.DataSource = v_ds_dm_don_vi.DM_DON_VI;
                m_cbo_bo_tinh.ValueMember = DM_DON_VI.ID;
                m_cbo_bo_tinh.DisplayMember = DM_DON_VI.TEN_DON_VI;



            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }
        private void load_data_to_cbo_don_vi_chu_quan()
        {
            try
            {
                if (m_cbo_bo_tinh.SelectedValue == null)
                {
                    m_cbo_don_vi_chu_quan.DataSource = null;
                    m_cbo_don_vi_su_dung.DataSource = null;
                    m_cbo_dia_chi.DataSource = null;
                    return;
                }
                DS_DM_DON_VI v_ds_dm_don_vi = new DS_DM_DON_VI();
                US_DM_DON_VI v_us_dm_don_vi = new US_DM_DON_VI();

                v_us_dm_don_vi.FillDataset(v_ds_dm_don_vi, "where ID_LOAI_DON_VI = 575 and ID_DON_VI_CAP_TREN  = " + CIPConvert.ToDecimal(m_cbo_bo_tinh.SelectedValue));
                m_cbo_don_vi_chu_quan.DataSource = v_ds_dm_don_vi.DM_DON_VI;
                m_cbo_don_vi_chu_quan.ValueMember = DM_DON_VI.ID;
                m_cbo_don_vi_chu_quan.DisplayMember = DM_DON_VI.TEN_DON_VI;

            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }
        private void load_data_to_cbo_don_vi_su_dung()
        {
            try
            {
                if (m_cbo_don_vi_chu_quan.SelectedValue == null)
                {
                    m_cbo_don_vi_su_dung.DataSource = null;
                    m_cbo_dia_chi.DataSource = null;
                    return;

                }
                DS_DM_DON_VI v_ds_dm_don_vi = new DS_DM_DON_VI();
                US_DM_DON_VI v_us_dm_don_vi = new US_DM_DON_VI();
                v_us_dm_don_vi.FillDataset(v_ds_dm_don_vi, "where ID_LOAI_DON_VI = 576 and ID_DON_VI_CAP_TREN = " + CIPConvert.ToDecimal(m_cbo_don_vi_chu_quan.SelectedValue));
                m_cbo_don_vi_su_dung.DataSource = v_ds_dm_don_vi.DM_DON_VI;
                m_cbo_don_vi_su_dung.DisplayMember = DM_DON_VI.TEN_DON_VI;
                m_cbo_don_vi_su_dung.ValueMember = DM_DON_VI.ID;
            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }
        private void load_data_to_cbo_dia_chi_dat()
        {
            try
            {
                if (m_cbo_don_vi_chu_quan.SelectedValue == null || m_cbo_don_vi_su_dung.SelectedValue == null)
                {
                    m_cbo_dia_chi.DataSource = null;
                    return;
                }
                m_us_dm_dat.FillDataset(m_ds_dm_dat, "where ID_DON_VI_CHU_QUAN = " + CIPConvert.ToDecimal(m_cbo_don_vi_chu_quan.SelectedValue) +
            " and id_don_vi_su_dung = " + CIPConvert.ToDecimal(m_cbo_don_vi_su_dung.SelectedValue));
                m_cbo_dia_chi.DataSource = m_ds_dm_dat.DM_DAT;
                m_cbo_dia_chi.DisplayMember = DM_DAT.DIA_CHI;
                m_cbo_dia_chi.ValueMember = DM_DAT.ID;
            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }
        private void load_data_to_cbo_trang_thai()
        {
            try
            {
                m_us_cm_dm_tu_dien.FillDataset(m_ds_cm_dm_tu_dien, "where id_loai_tu_dien = 8");
                m_cbo_trang_thai.DataSource = m_ds_cm_dm_tu_dien.CM_DM_TU_DIEN;
                m_cbo_trang_thai.DisplayMember = CM_DM_TU_DIEN.TEN;
                m_cbo_trang_thai.ValueMember = CM_DM_TU_DIEN.ID;
            }
            catch (System.Exception ex)
            {
                CSystemLog_301.ExceptionHandle(ex);
            }
        }
        private ITransferDataRow get_trans_object(C1.Win.C1FlexGrid.C1FlexGrid i_fg)
        {
            Hashtable v_htb = new Hashtable();
            v_htb.Add(DM_NHA.NGAY_THANG_NAM_SU_DUNG, e_col_Number.NGAY_THANG_NAM_SU_DUNG);
            v_htb.Add(DM_NHA.NGUON_NS, e_col_Number.NGUON_NS);
            v_htb.Add(DM_NHA.CO_SO_HDSN, e_col_Number.CO_SO_HDSN);
            v_htb.Add(DM_NHA.CHO_THUE, e_col_Number.CHO_THUE);
            v_htb.Add(DM_NHA.TONG_DT_SAN_XD, e_col_Number.TONG_DT_SAN_XD);
            //v_htb.Add(DM_NHA.ID_DAT, e_col_Number.ID_DAT);
            v_htb.Add(DM_NHA.GIA_TRI_CON_LAI, e_col_Number.GIA_TRI_CON_LAI);
            v_htb.Add(DM_NHA.NAM_XAY_DUNG, e_col_Number.NAM_XAY_DUNG);
            //v_htb.Add(DM_NHA.ID_DON_VI_SU_DUNG, e_col_Number.ID_DON_VI_SU_DUNG);
            //v_htb.Add(DM_NHA.ID_DON_VI_CHU_QUAN, e_col_Number.ID_DON_VI_CHU_QUAN);
            //v_htb.Add(DM_NHA.ID_DON_VI_DAU_TU, e_col_Number.ID_DON_VI_DAU_TU);
            v_htb.Add(DM_NHA.CAP_HANG, e_col_Number.CAP_HANG);
            //v_htb.Add(DM_NHA.ID_TRANG_THAI, e_col_Number.ID_TRANG_THAI);
            v_htb.Add(DM_NHA.KHAC, e_col_Number.KHAC);
            v_htb.Add(DM_NHA.DT_XAY_DUNG, e_col_Number.DT_XAY_DUNG);
            //v_htb.Add(DM_NHA.ID_NGUOI_LAP, e_col_Number.ID_NGUOI_LAP);
            v_htb.Add(DM_NHA.LAM_NHA_O, e_col_Number.LAM_NHA_O);
            v_htb.Add(DM_NHA.SO_TANG, e_col_Number.SO_TANG);
           // v_htb.Add(DM_NHA.ID_LOAI_TAI_SAN, e_col_Number.ID_LOAI_TAI_SAN);
            v_htb.Add(DM_NHA.TEN_TAI_SAN, e_col_Number.TEN_TAI_SAN);
           // v_htb.Add(DM_NHA.ID, e_col_Number.ID);
            v_htb.Add(DM_NHA.BI_LAN_CHIEM, e_col_Number.BI_LAN_CHIEM);
          //  v_htb.Add(DM_NHA.NGAY_CAP_NHAT_CUOI, e_col_Number.NGAY_CAP_NHAT_CUOI);
            //v_htb.Add(DM_NHA.MA_TAI_SAN, e_col_Number.MA_TAI_SAN);
            v_htb.Add(DM_NHA.BO_TRONG, e_col_Number.BO_TRONG);
            v_htb.Add(DM_NHA.TRU_SO_LAM_VIEC, e_col_Number.TRU_SO_LAM_VIEC);
            //v_htb.Add(DM_NHA.ID_NGUOI_DUYET, e_col_Number.ID_NGUOI_DUYET);
            v_htb.Add(DM_NHA.NGUON_KHAC, e_col_Number.NGUON_KHAC);

            ITransferDataRow v_obj_trans = new CC1TransferDataRow(i_fg, v_htb, m_ds.DM_NHA.NewRow());
            return v_obj_trans;
        }
        private void load_data_2_grid()
        {
            m_ds = new DS_DM_NHA();
            m_us.FillDataset(m_ds);
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            m_fg.Redraw = true;
        }
        private void grid2us_object(US_DM_NHA i_us
            , int i_grid_row)
        {
            DataRow v_dr;
            v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            m_obj_trans.GridRow2DataRow(i_grid_row, v_dr);
            i_us.DataRow2Me(v_dr);
        }


        private void us_object2grid(US_DM_NHA i_us
            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


        private void insert_dm_nha()
        {
            //	f399_DM_NHA_DE v_fDE = new  f399_DM_NHA_DE();								
            //	v_fDE.display();
            load_data_2_grid();
