            v_htb.Add(V_DM_NHA.NAM_XAY_DUNG, e_col_Number.NAM_XAY_DUNG);
            v_htb.Add(V_DM_NHA.CAP_HANG, e_col_Number.CAP_HANG);
            v_htb.Add(V_DM_NHA.KHAC, e_col_Number.KHAC);
            v_htb.Add(V_DM_NHA.DT_XAY_DUNG, e_col_Number.DT_XAY_DUNG);
            v_htb.Add(V_DM_NHA.LAM_NHA_O, e_col_Number.LAM_NHA_O);
            v_htb.Add(V_DM_NHA.SO_TANG, e_col_Number.SO_TANG);
            v_htb.Add(V_DM_NHA.TEN_TAI_SAN, e_col_Number.TEN_TAI_SAN);
            v_htb.Add(V_DM_NHA.BI_LAN_CHIEM, e_col_Number.BI_LAN_CHIEM);
            v_htb.Add(V_DM_NHA.BO_TRONG, e_col_Number.BO_TRONG);
            v_htb.Add(V_DM_NHA.TRU_SO_LAM_VIEC, e_col_Number.TRU_SO_LAM_VIEC);
