            , int i_grid_row)
        {
            DataRow v_dr = (DataRow)m_fg_oto.Rows[i_grid_row].UserData;
            i_us.Me2DataRow(v_dr);
            m_obj_trans.DataRow2GridRow(v_dr, i_grid_row);
        }


