        private void delete_v_dm_nha()
        {
            if (!CGridUtils.IsThere_Any_NonFixed_Row(m_fg_nha)) return;
            if (!CGridUtils.isValid_NonFixed_RowIndex(m_fg_nha, m_fg_nha.Row)) return;
            if (BaseMessages.askUser_DataCouldBeDeleted(8) != BaseMessages.IsDataCouldBeDeleted.CouldBeDeleted) return;
            US_DM_NHA v_us = new US_DM_NHA();
            grid2us_object(v_us, m_fg_nha.Row);
            try
            {
                v_us.BeginTransaction();
                v_us.Delete();
                v_us.CommitTransaction();
                m_fg_nha.Rows.Remove(m_fg_nha.Row);
            }
            catch (Exception v_e)
            {
                v_us.Rollback();
                CDBExceptionHandler v_objErrHandler = new CDBExceptionHandler(v_e,
                    new CDBClientDBExceptionInterpret());
                v_objErrHandler.showErrorMessage();
            }
        }

