                v_dtr[11] = m_fg_excel_nha.GetData(row, (int)e_col_Excel_number.TRU_SO_LAM_VIEC);
                v_dtr[12] = m_fg_excel_nha.GetData(row, (int)e_col_Excel_number.CO_SO_HDSN);
